import React, { Component } from "react";
import { Button, Form } from "react-bootstrap";
import EventEditService from "../../service/Events.service";
// import FilesService from '../../service/Files.service'
class EventEdit extends Component {
  constructor(props) {
    super(props);
    console.log("<<<------Aquuí------>>>>", props);
    this.service = new EventEditService();
    // // this._filesService = new FilesService()
    this.state = {
      buttonText: "editar plan",
      event: {
        nameEvent: this.props.event.nameEvent,
        category: this.props.event.category,
        email: this.props.event.email,
        town: this.props.event.town,
        capacityPlace: this.props.event.capacityPlace,
        description: this.props.event.description
      }
    };
  }
  handleSubmit = e => {
    e.preventDefault();
    this.service
      .EventEdit(this.state.event, this.props.event._id)
      .then(theEditedEvent => {
        console.log("<------AQUI-------->", this.props.updateEventsList);
        this.setState(
          {
            nameEvent: "",
            category: "",
            email: "",
            town: "",
            capacityPlace: "",
            description: ""
          },
          () => this.props.updateEventsList(theEditedEvent.data)
        );
        this.props.closeModalWindow();
        this.props.history.push("/edit");
      })
      .catch(err => console.log(err));
    // handleSubmit = e => {
    //         e.preventDefault()
    //         this.EventsService.postEvent(this.state.event)
    //             .then(x => {
    //                 this.props.closeModalWindow()
    //                 this.props.updateEventsList()
    //             })

    //             .catch(err => console.log(err))
  };
  handleInputChange = e => {
    let { name, value } = e.target;
    this.setState({
      match: { ...this.state.event, [name]: value }
    });
  };
  render() {
    return (
      <Form onSubmit={this.handleSubmit}>
        <Form.Group>
          <Form.Label>Nombre del evento</Form.Label>
          <Form.Control
            type="text"
            name="nameEvent"
            onChange={this.handleInputChange}
            value={this.state.event.nameEvent}
          />
        </Form.Group>
        <Form.Group>
          <Form.Label>Evento</Form.Label>
          <Form.Control
            type="text"
            name="category"
            onChange={this.handleInputChange}
            value={this.state.event.category}
          />
        </Form.Group>

        <Form.Group>
          <Form.Label>Descripción</Form.Label>
          <Form.Control
            as="textarea"
            name="description"
            onChange={this.handleInputChange}
            value={this.state.event.description}
          />
        </Form.Group>
        <Form.Group>
          <Form.Label>Ciudad</Form.Label>
          <Form.Control
            type="text"
            name="town"
            onChange={this.handleInputChange}
            value={this.state.event.town}
          />
        </Form.Group>
        <Form.Group>
          <Form.Label>Capacidad de aforo</Form.Label>
          <Form.Control
            type="number"
            name="capacityPlace"
            onChange={this.handleInputChange}
            value={this.state.event.capacityPlace}
          />
        </Form.Group>
        <Form.Group>
          <Form.Label>Correo electrónico</Form.Label>
          <Form.Control
            type="email"
            name="email"
            onChange={this.handleInputChange}
            value={this.state.event.email}
          />
        </Form.Group>

        <Button
          variant="dark"
          size="sm"
          type="submit"
          disabled={this.state.disabledButton}
        >
          {this.state.buttonText}
        </Button>
      </Form>
    );
  }
}
export default EventEdit;
