import React from 'react'
import Col from 'react-bootstrap/Col'
import SpacesEdit from '../pages/Space-edit'
import { Container, Row, Button, Modal } from "react-bootstrap";

import { Link } from 'react-router-dom'

class SpaceCard extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            showModalWindow: false
        }
    }

    handleShow = () => this.setState({ showModalWindow: true })
    handleClose = () => this.setState({ showModalWindow: false })
    render() {
        return (
          <Col md={4}>
            <h3><strong>{this.props.nameSpace}</strong></h3>
            <h4><strong>Ciudad:</strong>{this.props.town}</h4>
            <h5><strong>Lugar:</strong>{this.props.place}</h5>
            <h5><strong>Superficie: </strong>{this.props.surface}</h5>
            <h6><strong>Aforo:</strong>{this.props.capacityPlace}</h6>
            <p><strong>Descripción:</strong>{this.props.description}</p>
            <br></br>
            {/* boton editar jugador  */}
            <Button variant="dark" onClick={this.handleShow}>
              Editar
            </Button>
            {/* boton borrar jugador */}
            <Button
              variant="dark"
              onClick={() => this.props.deleteMatch(this.props._id)}
            >
              Eliminar
            </Button>

            <Modal show={this.state.showModalWindow} onHide={this.handleClose}>
              <Modal.Header closeButton>
                <Modal.Title>Editar espacio</Modal.Title>
              </Modal.Header>
              <Modal.Body>
                <SpacesEdit
                //   match={this.props}
                  updateSpacesList={this.props.updateSpacesList}
                  closeModalWindow={this.handleClose}
                />
                {/* <deletePlayerplayer={this.props} updatePlayersList={this.props.updatePlayersList} closeModalWindow={this.handleClose} /> */}
              </Modal.Body>
            </Modal>
          </Col>
        );
    }
}


export default SpaceCard

