import React, { Component } from "react";
import { Button, Form, Container } from "react-bootstrap";


import Service from "../../service/Auth.service";

class SignupForm extends Component {
  constructor(props) {
    super(props);
    this._service = new Service();
    this.state = { username: "", password: "", lastName:"", role: "" };
  }

  handleSubmit = e => {
    e.preventDefault();
    const { username, password, lastName, role } = this.state;
    this._service
      .signup(username, password, lastName, role)
      .then(theNewUser => {
          console.log(`este es el nuevo user ${theNewUser.data}`)
        this.props.setUser(theNewUser.data);
        this.setState({ username: "", password: "", lastName: "", role: ""});
        console.log(`soy el role del nuevo usuario`, this.state)
        this.props.history.push("/"); // REDIRECCIONAMIENTO

      })
      .catch(err => console.log(err.response.data.message));
  };

  handleInputChange = e => {
    let { name, value } = e.target;
    this.setState({ [name]: value });
  };

  render() {
    return (
      <Container>
        <h1>Registro</h1>

        <Form onSubmit={this.handleSubmit}>
          <Form.Group>
            <Form.Label>Nombre Usuario</Form.Label>
            <Form.Control
              type="text"
              name="username"
              onChange={this.handleInputChange}
              value={this.state.username}
            />
          </Form.Group>
          <Form.Group>
            <Form.Label>Contraseña</Form.Label>
            <Form.Control
              type="password"
              name="password"
              onChange={this.handleInputChange}
              value={this.state.password}
            />
          </Form.Group>
          <Form.Group>
            <Form.Label>Apellido</Form.Label>
            <Form.Control
              type="text"
              name="lastName"
              onChange={this.handleInputChange}
              value={this.state.lastName}
            />
          </Form.Group>
          <Form.Group>
            <Form.Label>Tipo de Usuario</Form.Label>
            <br/>
            <select
              type="select"
              name="role"
              onChange={this.handleInputChange}
              selected={this.state.role}>
              <option value="explorer">Buscador de Planes</option>
              <option value="host">Anfitrión</option>
              <option value="creator">Creador</option>
            </select>
          </Form.Group>
          <Button variant="dark" type="submit">
            Registrarme
          </Button>
        </Form>
      </Container>
    );
  }
}

export default SignupForm;
