import axios from 'axios'

export default class Services {

    constructor() {
        this._service = axios.create({
            baseURL: `${process.env.REACT_APP_URL}`,
            withCredentials: true // RUTAS PERSISTENTES
        })
    }

    signup = (username, password, lastName, role) => this._service.post('/auth/signup', {username,password,lastName,role})
    login = (username, password) => this._service.post('/auth/login', {
        username,
        password
    })
    logout = () => this._service.post('/logout')
    loggedin = () => this._service.get('/loggedin')
}
