import React, { Component } from "react";
import EventsDetailsService from '../../service/Events.service'
import { Container, Row, Col } from 'react-bootstrap'
import { Link } from 'react-router-dom'

class EventDetail extends Component {

    constructor(props) {
        super(props)
        this.state = {event: {}} 
        this._service = new EventsDetailsService()
        console.log(this.state)
    }

componentDidMount = () =>{
    const eventId = this.props.match.params._id
    this._service.getOneEvent(eventId)
    .then(theEvent => this.setState({event: theEvent.data}))
    .catch(err => console.log(err))
}

    componentDidMount (){
        const eventId= this.props.match.params
        this._service.getOneEvent(eventId)
            .then(theEvent => this.setState({ event: theEvent.data }))
            .catch(err => console.log(err))
    }

    render() {
        return (
            <Container >
                <section>
                    <Row>
                        <Col md={6}>
                            <h1> {this.state.event.nameEvent}</h1>
                            <h3><strong>Ciudad: </strong> {this.state.event.town}</h3>
                            <p><strong>Descripci√≥n:</strong> {this.state.event.description}</p>
                            <hr></hr>
                            <p><strong>Aforo:</strong> {this.state.event.capacityPlace} | <strong>Superficie:</strong> {this.state.event.surface}</p>
                            <Link to="/creator" className="btn btn-dark">Volver</Link>
                        </Col>
                        <Col md={{ span: 4, offset: 2 }}>
                            {/* <img src={this.state.coaster.imageUrl} alt={this.state.coaster.title}></img> */}
                        </Col>
                    </Row>
                </section>
            </Container>
        )
    }

}

export default EventDetail
