import React, { Component } from "react";
import { Container, Button, Form } from "react-bootstrap";

import SpacesService from "../../service/Spaces.service";

class SpaceForm extends Component {
  constructor(props) {
    super(props);
    this.SpacesService = new SpacesService();
    this.state = {
      disabledButton: false,
      buttonText: "Crear un espacio",
      space: {
        nameSpace: "",
        place: "",
        surface: 0,
        capacityPlace: 0,
        description: "",
        town: ""
      }
    };
  }

  handleSubmit = e => {
    e.preventDefault();
    this.SpacesService.postEvent(this.state.space)
      .then(x => {
        // this.props.closeModalWindow()
        // this.props.updateEventList()
        // this.props.history.push("/profile/creator");
        this.props.history.push("/profile/creator");
      })
      .catch(err => console.log(err));
  };

  handleInputChange = e => {
    let { name, value } = e.target;
    this.setState({
      event: { ...this.state.event, [name]: value }
    });
  };

  // handleFileUpload = e => {
  //     this.setState({ disabledButton: true, buttonText: 'Subiendo imagen...' })

  //     const uploadData = new FormData()
  //     uploadData.append("imageUrl", e.target.files[0])
  //     this._filesService.handleUpload(uploadData)
  //         .then(response => {
  //             console.log('Subida de archivo finalizada! La URL de Cloudinray es: ', response.data.secure_url)
  //             this.setState({
  //                 disabledButton: false,
  //                 buttonText: 'Crear monta침a rusa',
  //                 coaster: { ...this.state.coaster, imageUrl: response.data.secure_url }
  //             })
  //         })
  //         .catch(err => console.log(err))
  // }

  render() {
    return (
      <Container>
        <Form onSubmit={this.handleSubmit}>
          <Form.Group>
            <Form.Label>Nombre del espacio</Form.Label>
            <Form.Control
              type="text"
              name="place"
              onChange={this.handleInputChange}
              value={this.state.event.place}
            />
          </Form.Group>
          <Form.Group>
            <Form.Label>Descripci칩n</Form.Label>
            <Form.Control
              as="textarea"
              name="description"
              onChange={this.handleInputChange}
              value={this.state.event.description}
            />
          </Form.Group>
          <Form.Group>
            <Form.Label>Superficie</Form.Label>
            <Form.Control
              type="number"
              name="surface"
              onChange={this.handleInputChange}
              value={this.state.event.town}
            />
          </Form.Group>
          <Form.Group>
            <Form.Label>Capacidad de aforo</Form.Label>
            <Form.Control
              type="number"
              name="capacityPlace"
              onChange={this.handleInputChange}
              value={this.state.event.capacityPlace}
            />
          </Form.Group>
          <Form.Group>
            <Form.Label>Correo electr칩nico</Form.Label>
            <Form.Control
              name="email"
              type="email"
              onChange={this.handleInputChange}
              value={this.state.event.email}
            />
          </Form.Group>
          <Form.Group>
            <Form.Label>Ciudad</Form.Label>
            <Form.Control
              as="text"
              name="town"
              onChange={this.handleInputChange}
              value={this.state.event.town}
            />
          </Form.Group>
          <Button
            variant="dark"
            size="sm"
            type="submit"
            disabled={this.state.disabledButton}
          >
            {this.state.buttonText}
          </Button>
        </Form>
      </Container>
    );
  }
}

export default SpaceForm;
