import React from 'react'
import Col from 'react-bootstrap/Col'

import { Link } from 'react-router-dom'

class SpaceCard extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            showModalWindow: false
        }
    }

    handleShow = () => this.setState({ showModalWindow: true })
    handleClose = () => this.setState({ showModalWindow: false })
    render() {
        return (
          <Col md={4}>
            <p>{this.props.nameSpace}</p>
            <p>{this.props.town}</p>
            <p>{this.props.place}</p>
            <p>{this.props.surface}</p>
            <p>{this.props.result}</p>
            <p>{this.props.season}</p>
            <br></br>
            {/* boton editar jugador  */}
            <Button variant="dark" onClick={this.handleShow}>
              Editar
            </Button>
            {/* boton borrar jugador */}
            <Button
              variant="dark"
              onClick={() => this.props.deleteMatch(this.props._id)}
            >
              Eliminar
            </Button>

            <Modal show={this.state.showModalWindow} onHide={this.handleClose}>
              <Modal.Header closeButton>
                <Modal.Title>Editar partido</Modal.Title>
              </Modal.Header>
              <Modal.Body>
                <MatchsEdit
                  match={this.props}
                  updateMatchsList={this.props.updateMatchsList}
                  closeModalWindow={this.handleClose}
                />
                {/* <deletePlayerplayer={this.props} updatePlayersList={this.props.updatePlayersList} closeModalWindow={this.handleClose} /> */}
              </Modal.Body>
            </Modal>
          </Col>
        );
    }
}


export default SpaceCard

