import React from "react";
import EventService from "../../service/Events.service";

import { Container, Row, Button, Modal } from "react-bootstrap";

import EventCard from "./Events-card";
import EventForm from "./Events-form";

class EventList extends React.Component {
  // el hijo no puede traer nada del padre sin el props
  constructor(props) {
    super(props);
    this._EventService = new EventService();
    this.state = {
      loggedInUser: props.loggedInUser,
      events: [],
      showModalWindow: false
    };
  }
  componentDidMount = () => this.updateEventsList();

  updateEventsList = () => {
    console.log("este es el user del event-lidt", this.state.loggedInUser._id);
    this._EventService
      .getAllEvents()
      .then(allEventsFromDB => this.setState({ events: allEventsFromDB.data }))
      .catch(err => console.log("Error", err));
  };

//deleteEvent = () => {
//this._EventService
//.deleteEvent()
//.then()
//
//}






  handleShow = () => this.setState({ showModalWindow: true });
  handleClose = () => this.setState({ showModalWindow: false });

  render() {
    return (
      <section>
        <Container>
          <h1>Este son los planes , mira y unete alguno... </h1>

          {/* <Button variant="dark" onClick={this.handleShow}>Añadir evento</Button> */}

          <Row>
            {this.state.events.map(event => (
              <EventCard
                key={event._id}
                {...event}
                updateEventsList={this.updateEventsList}
              />
            ))}
          </Row>
        </Container>

        <Modal show={this.state.showModalWindow} onHide={this.handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Añadir nuevo evento</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <EventForm
              closeModalWindow={this.handleClose}
              updateEventsList={this.updateEventsList}
              loggedInUser={this.state.loggedInUser}
            />
          </Modal.Body>
        </Modal>
      </section>
    );
  }
}

export default EventList;
